def render_text_on_matrix(ledmatrix, text, start_row, start_col, max_chars, letters_temp):
    char_count = 0
    # Process each character in the input text
    for char in text:
        # Check if we have drawn the maximum number of characters
        if char_count >= max_chars:
            break

        # Get the character pattern from letters_temp, converting to lowercase.
        # If a character isn't found (like an emoji), default to a space.
        pattern = letters_temp.get(char.lower(), letters_temp[" "])

        # A character is 5 rows tall and 3 columns wide
        for row_index in range(5):
            for col_index in range(3):
                # Calculate the target position in the main LED matrix
                matrix_row = start_row + row_index
                matrix_col = start_col + (char_count * 4) + col_index

                # Assign a color based on the pattern's 0 or 1
                if pattern[row_index][col_index] == 1:
                    # If it's a 1, make the pixel white
                    ledmatrix[matrix_row][matrix_col] = ["255", "255", "255"]
                else:
                    # If it's a 0, make the pixel black (off)
                    ledmatrix[matrix_row][matrix_col] = ["0", "0", "0"]

        # This part adds the empty 4th column to create space between letters
        for row_index in range(5):
            matrix_row = start_row + row_index
            matrix_col = start_col + (char_count * 4) + 3
            ledmatrix[matrix_row][matrix_col] = ["0", "0", "0"]

        char_count += 1

def render_bubbles(ledmatrix, filled_bubbles, start_row, start_col, max_cols):
    bubble_count = 0
    for col in range(max_cols):
        for row in range(3):
            ledmatrix[start_row + row][start_col + col] = (
                ["255", "255", "255"] if bubble_count < filled_bubbles else ["0", "0", "0"]
            )
            bubble_count += 1

def print_led_matrix(ledmatrix):
    for row in ledmatrix:
        print("".join(f"\x1b[48;2;{cell[0]};{cell[1]};{cell[2]}m   \x1b[0m" for cell in row))

def printPlayback():
    while True:
        os.system("cls")

        currTime = datetime.now()

        weekday = currTime.strftime("%a")
        month = currTime.strftime("%b")
        year = currTime.strftime("%Y")
        day = currTime.strftime("%d")
        hour = currTime.strftime("%I")
        minute = currTime.strftime("%M")
        second = currTime.strftime("%S")
        am_pm = currTime.strftime("%p")
        
        dayTime = " ".join([weekday, ":".join([hour, minute]), am_pm])
        date = " ".join([month, day, year])


        render_text_on_matrix(ledmatrix, dayTime, 0, 0, 16, letters_temp)
        render_text_on_matrix(ledmatrix, date, 6, 0, 16, letters_temp)

        print_led_matrix(ledmatrix)
        print("Updated!")
        time.sleep(60 - int(second))
    
      
        
printPlayback()
  